#include<iostream>
#include<string>
#include<vector>
#include<unordered_map>

using namespace std;

class Solution {
 public:
  bool worker(string s, vector<string>& wordDict,
              unordered_map<string, bool>& memo) {
    if (s.length() == 0) return true;
    auto it = memo.find(s);
    if (it != memo.end()) {
      return it->second;
    }
    bool found = false;
    string left, right;
    for (const auto& word : wordDict) {
      auto pos = s.find(word);
      if (pos != string::npos) {
        left = s.substr(0, pos);
        right = s.substr(pos + word.length());
        //memo[s] = true;
        if (worker(left, wordDict, memo) && worker(right, wordDict, memo)) {
          memo[s] = true;
          found = true;
          break;
        }
      } else {
        memo[s] = false;
      }
    }
    if (found) {
      return true;
    } else {
      return false;
    }
  }

  bool wordBreak(string s, vector<string>& wordDict) {
    unordered_map<string, bool> memo;
    return worker(s, wordDict, memo);
  }
};

